mapper.select:
SELECT TO_CHAR(c.co_start_date,'YYYY-MM-DD') AS co_start_date ,TO_CHAR(c.end_date,'YYYY-MM-DD')AS end_date ,c.co_ing_status ,c.co_release ,c.co_disease ,a.d_name ,d.ma_name ,c.co_no ,e.p_name FROM doctor a ,co_med c ,major d ,patient e WHERE a.d_id = c.co_doc AND a.ma_no = d.ma_no AND c.p_no = e.p_no
and a.d_name = #{dName}
and c.co_disease = #{coDisease}
and p_name like '%'||#{keyword}||'%'
and a.d_name =#{dName} or c.co_disease=#{coDisease} or a.p_name = #{pName}
order by co_start_date DESC

SELECT COUNT(*) FROM( SELECT TO_CHAR(c.co_start_date,'YYYY-MM-DD') AS co_start_date ,TO_CHAR(c.end_date,'YYYY-MM-DD')AS end_date ,c.co_ing_status ,c.co_release ,c.co_disease ,a.d_name ,d.ma_name ,c.co_no ,e.p_name FROM doctor a ,co_med c ,major d ,patient e WHERE a.d_id = c.co_doc AND a.ma_no = d.ma_no AND c.p_no = e.p_no
and a.d_name = #{dName}
and c.co_disease = #{coDisease}
and a.d_name =#{dName} or c.co_disease=#{coDisease}
order by co_start_date DESC )

select count(*) from( select to_char(C.co_start_date,'YYYY-MM-DD')as co_start_date, C.co_release, C.co_disease, C.co_no, E.p_name, A.d_name, D.ma_name from doctor A, co_med C, major D, patient E where A.d_id=C.CO_DOC and A.ma_no=D.ma_no and c.p_no = e.p_no and C.co_ing_status='1'
and a.d_name = #{dName}
and c.co_disease = #{coDisease}
and a.d_name =#{dName} or c.co_disease=#{coDisease}
order by C.co_start_date DESC)
