mapper.select:
		 select symptom
          from medi_reco
         where m_no = #{mNo}

	select c.iss_no
  	  from medi_reco a, issue b, issue_readyn c
      where a.iss_no = b.iss_no
        and b.iss_no = c.iss_no
        and a.m_no= #{mNo}
        and c.d_id =#{dId}

mapper.update:
		update issue_readyn
   		   set iss_yn= 'N'
 		 where iss_no = #{issNo}
 		 and d_id = #{dId}
