mapper.update:
INSERT INTO sub_d (
sub_d_name,
m_no
) VALUES (
#{subDName},
#{mNo}
)

INSERT INTO issue (
iss_no,
iss_content
) VALUES (
#{issNo},
#{issContent}
)

INSERT INTO med_pic (
mp_no,
m_no,
mp_name
) VALUES (
#{mpNo},
#{mNo},

#{mpName}
)

INSERT INTO medi_file (
mf_no,
m_no,
mf_filename
) VALUES (
#{mfNo},
#{mNo},
#{mfFilename}
)

mapper.select:
select mf_no, mf_filename
from medi_file
where m_no = #{mNo}

select mp_name from med_pic
where
mp_no = #{mpNo}

select mf_filename from
medi_file
where mf_no = #{mfNo}

mapper.update:
UPDATE my_order
SET
m_no = #{mNo}
WHERE
mo_no = #{moNo}
