mapper.select:
select o_drug, quantity, dg_aday, dg_days
from my_drug
where mo_no = #{moNo}

select sub_d_name
from sub_d
where m_no = #{mNo}

select drug_nm from dur where drug_nm = #{drugNm}
union all
select '0' as drug_nm from dual where not exists( select drug_nm from dur where drug_nm = #{drugNm} )
