mapper.select:
select o_dname, mo_no
from my_order
where doc_id = #{dId}

mapper.update:
INSERT INTO my_drug (
        o_drug
       ,mo_no
       ,quantity
       , dg_aday
       , dg_days
    ) VALUES (
        #{oDrug}
      , #{moNo}
      , #{quantity}
       ,#{dgAday}
      , #{dgDays}
    )

		    update my_order
            set O_RESULT = #{oResult}
            where mo_no = #{moNo}

update my_order
   set  o_dname = #{oDname}, o_target=#{oTarget}, o_regdate= sysdate, o_title = #{oTitle}, o_noti = #{oNoti}
   where mo_no = #{moNo}

update my_order
   set  del_yn= 'Y'
   where mo_no = #{moNo}

UPDATE my_order
    SET
        o_caution = #{oCaution}
WHERE
    mo_no = #{moNo}
