mapper.update:
INSERT INTO drug_orderset (
    o_no
    , doc_id
    , o_dname
    , o_viewcnt
    , o_cla
    , o_target
    , o_regdate
    , o_caution
    , o_result
    , del_yn
    , o_title
    , o_noti
) VALUES (
    #{oNo}
  , #{docId}
  , #{oDname}
  , 0
  , #{oCla}
  , #{oTarget}
  , sysdate
  , #{oCaution}
  , #{oResult}
  , 'N'
  , #{oTitle}
  , #{oNoti}
)

    	  INSERT
		    INTO drugs (
		        o_drug
		        , o_no
		        , quantity
		        , dg_aday
		        , dg_days
		    ) VALUES (
		        #{oDrug}
		      , #{oNo}
		      , #{quantity}
		      , #{dgAday}
		      , #{dgDays}
		    )

   DELETE FROM DRUG_RECOM
	WHERE O_NO = #{oNo}
	  and d_id = #{dId}

mapper.select:
			(SELECT  1
			  FROM drug_recom d
			 WHERE d.o_no = #{oNo}
			   AND d.d_id = #{dId}
             )
              union all

              select 0
              from dual
            where not exists
            (SELECT  1
			  FROM drug_recom c
			 WHERE c.o_no = #{oNo}
			   AND c.d_id = #{dId}
             )

mapper.update:
      INSERT INTO order_feed (
	        fb_no
	       ,o_no
	       ,doc_id
	       ,fb_result
	       ,fb_regdate
	    ) VALUES (
	        'OF' || lpad(osf_seq.NEXTVAL,3,'0')
	       ,#{oNo}
	       ,#{docId}
	       ,#{fbResult}
	       ,SYSDATE
	    )

update drug_orderset
   set  o_dname = #{oDname}, o_target=#{oTarget}, o_regdate= sysdate, o_caution =#{oCaution}, o_result=#{oResult}, o_title = #{oTitle}, o_noti = #{oNoti}
   where o_no = #{oNo}

update drug_orderset
   set  del_yn= 'Y'
   where o_no = #{oNo}

mapper.select:
   	  select me_name
	    from medicine 
	   where me_name = #{meName}
